secretaria_bytes = sum(len(s.encode('utf-8')) for s in secretarias)
assunto_bytes = sum(len(a.encode('utf-8')) for a in assuntos)
secretaria_assunto_bytes = sum(len(sa.encode('utf-8')) for sa in secretaria_assunto)
bairro_bytes = sum(len(b.encode('utf-8')) for b in bairros)
categoria_bytes = sum(len(c.encode('utf-8')) for c in categorias)
status_bytes = sum(len(s.encode('utf-8')) for s in status)
int_bytes = 4

# DIMENSIONAL TABLES (BYTES)
dCategoria = (len(categorias) * int_bytes) + (categoria_bytes)
dBairro = (len(bairros) * int_bytes) + (bairro_bytes)
dStatus = (len(status) * int_bytes) + (status_bytes)
dAssunto = (len(assuntos) * int_bytes) + (assunto_bytes)
dSecretaria = (len(secretarias) * int_bytes) + (secretaria_bytes)

# FACT TABLES (WITH DIM)
fBairro_Categoria = 2 * (int_bytes * len(bairros) * len(categorias))
fBairro_Status = 2 * (int_bytes * len(bairros) * len(status))
fAssunto_Categoria = 3 * (int_bytes * len(assuntos) * len(categorias))
fAssunto_Status = 3 * (int_bytes * len(assuntos) * len(status))

# FACT TABLES (NO DIM)
fBairro_Categoria_den = (bairro_bytes * len(categorias)) + (categoria_bytes * len(bairros))
fBairro_Status_den = (bairro_bytes * len(status)) + (status_bytes * len(bairros))
fAssunto_Categoria_den = (secretaria_assunto_bytes * len(categorias)) + (categoria_bytes * len(assuntos))
fAssunto_Status_den = (secretaria_assunto_bytes * len(status)) + (status_bytes * len(assuntos))


# TOTAL
total_bytes = fBairro_Categoria + fBairro_Status + fAssunto_Status + fAssunto_Categoria + dBairro + dCategoria + dAssunto + dSecretaria
total_bytes_no_dim = fBairro_Categoria_den + fBairro_Status_den + fAssunto_Categoria_den + fAssunto_Status_den
# total_bytes_no_dim  = (bairro_bytes + secretaria_assunto_bytes) * (len(categorias) + len(status)) + (len(assuntos) + len(bairros)) * (categoria_bytes + status_bytes)

decimal_reduction_table = (fAssunto_Categoria_den - fAssunto_Categoria) / fAssunto_Categoria_den
percentage_reduction_table = decimal_reduction_table * 100

decimal_reduction_bd = (total_bytes_no_dim - total_bytes) / total_bytes_no_dim
percentage_reduction_bd = decimal_reduction_bd * 100

print(f"Bytes of fAssunto_Categoria before normalization: {fAssunto_Categoria_den / 1024:.2f} KB")
print(f"Bytes of fAssunto_Categoria after normalization: {(fAssunto_Categoria / 1024):.2f} KB")
print(f"Percentage reduction in storage of fAssunto_Catgeoria: {percentage_reduction_table:.2f}%")
print()
print(f"Total bytes before normalization: {(total_bytes_no_dim / 1024):.2f} KB")
print(f"Total bytes after normalization: {(total_bytes / 1024):.2f} KB")
print(f"Percentage reduction in database storage.: {percentage_reduction_bd:.2f}%")